import React, { useEffect, useState, useRef } from "react";
import { useDrag, useDrop } from "react-dnd";
// import { Parser } from "html-to-react";
import parse from "html-react-parser";
import "./template.css";

const htmlInput = ``;
// const htmlToReactParser = new Parser();
// const reactElement = htmlToReactParser.parse(htmlInput);
// console.log(reactElement.props.children);
// console.log(parse(htmlInput));

// assert.equal(reactHtml, htmlInput); // true

const Template = () => {
  // const [tempEditor, setTempEditor] = useState(null);
  // useEffect(() => {
  //   const editor = grapesjs.init({
  //     container: "#tempEditor",
  //   });
  //   setTempEditor(editor);
  // }, []);
  const [activeElementSelection, setActiveElementSelelection] = useState(true);
  return (
    <div style={{ height: "100vh" }}>
      <nav className="bg-primary">
        <div className="flex h-14 py-2 px-1 ml-80 w-1/12 justify-around">
          <i className="fa-solid fa-mobile text-4xl text-gray-200"></i>
          <i className="fa-solid fa-mobile text-4xl text-gray-200"></i>
          <i className="fa-solid fa-mobile text-4xl text-gray-200"></i>
        </div>
      </nav>
      <div className="flex" style={{ height: "92.5%" }}>
        <div
          style={{
            width: "23%",
          }}
          className="border-solid border border-black h-full bg-primary"
        >
          <div className="border-solid border-b p-2 border-inactive h-3/6">
            {activeElementSelection ? (
              <>
                <div className="flex justify-between">
                  <div className="text-white">Screens</div>
                  <div
                    className="mt-1 bg-secondary cursor-pointer"
                    style={{
                      borderRadius: "50%",
                      height: 20,
                      width: 20,
                      color: "black",
                      position: "relative",
                    }}
                  >
                    <span
                      style={{
                        fontSize: 20,
                        fontWeight: 600,
                        position: "absolute",
                        bottom: -1,
                        left: 3,
                        // border: "1px solid white",
                        padding: 0,
                      }}
                    >
                      +
                    </span>
                  </div>
                </div>
              </>
            ) : (
              <>
                <div class="bg-secondary text-white font-bold ml-10 my-5 py-2 px-5 w-60 rounded  text-center">
                  Button
                </div>
              </>
            )}
          </div>
          <div>
            <div className="flex justify-between px-2">
              <div></div>
              <div
                className="mt-1 bg-secondary cursor-pointer"
                style={{
                  borderRadius: "50%",
                  height: 20,
                  width: 20,
                  color: "black",
                  position: "relative",
                }}
                onClick={() =>
                  setActiveElementSelelection(!activeElementSelection)
                }
              >
                <span
                  style={{
                    fontSize: 20,
                    fontWeight: 600,
                    position: "absolute",
                    bottom: -1,
                    left: 3,
                    // border: "1px solid white",
                    padding: 0,
                  }}
                >
                  +
                </span>
              </div>
            </div>
          </div>
        </div>
        <div
          style={{
            width: "54%",
          }}
          className="border-solid border border-black h-full bg-inactive"
        >
          <div></div>
        </div>
        <div
          style={{
            width: "23%",
          }}
          className="border-solid border border-black h-full bg-primary"
        >
          <div>
            <div className="flex justify-between h-9">
              <div className="w-3/6 text-center pt-1 border-solid border-b border-secondary txt-secondary">
                Style
              </div>
              <div className="w-3/6 text-center pt-1 bg-inactive text-white">
                Conditions
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Template;
